import React from 'react';
import Select from 'react-select';
import Async, { makeAsyncSelect } from 'react-select/async';
// import Tabela from './Tabela';

export default class Form extends React.Component {
    constructor() {
        super();
        this.state = {
            nome: '',
            cidadeSelecionada: '',
            lista: [],
            cidade: ''
        };
    }

    componentDidMount() {
        fetch(`http://servicos.cptec.inpe.br/XML/listaCidades?city=${this.state.nome}`,{method:"GET"}, {
            mode: 'no-cors',
            method: 'GET',
            headers: new Headers({
                'Accept': 'application/xml',
                'content-type': 'application/x-www-form-urlencoded',
                'Access-Control-Allow-Origin': '*',
                'Access-Control-Allow-Methods': 'GET, POST, PUT',
                'Access-Control-Allow-Headers': 'Content-Type',
            })
        })
        .then(response => response.arrayBuffer())
        .then(buffer => {
            let decoder = new TextDecoder("iso-8859-1");
            return decoder.decode(buffer);
        })
        .then(str => { console.log("str: " + str);
            let parser = new window.DOMParser()
            let xml = parser.parseFromString(str, "text/xml");
            let cidades = xml.getElementsByTagName('cidades')[0]; // Obter a tag cidades
            let lista = [];
            cidades.childNodes.forEach((obj, index) => {
                lista.push(obj.childNodes[0].childNodes[0].nodeValue)
            });
            console.log("lista: " + lista);
        })
        .catch(erro => console.log(erro) );
    }


    render() {
        var selecao = '';
        return (
          <div>
            <select
              value={this.state.cidadeSelecionada}
              onChange={e =>
                this.setState({
                  selectedTeam: e.target.value,
                  validationError:
                    e.target.value === ""
                      ? "You must select your favourite team"
                      : ""
                })
              }
            >
                selecao = {this.state.lista.map(cid =>
                <option key={cid.key} value={cid.key}>{cid.value}</option>
                ).then(console.log(selecao))};
            </select>
            <div>
              {this.state.validationError}
            </div>
          </div>
        );
    }
}
